 /**
 * Post Authors Post Link Shortcode
 *
 * @author Bill Erickson
 * @link http://www.billerickson.net/wordpress-post-multiple-authors/
 * 
 * @param array $atts
 * @return string $authors
 */
function be_post_authors_shortcode( $atts ) {

	$atts = shortcode_atts( array( 
		'between'      => null,
		'between_last' => null,
		'before'       => null,
		'after'        => null
	), $atts );

	$authors = function_exists( 'coauthors' ) ? coauthors( $atts['between'], $atts['between_last'], $atts['before'], $atts['after'], false ) : $atts['before'] . get_the_author() . $atts['after'];
	return $authors;
}
add_shortcode( 'post_authors', 'be_post_authors_shortcode' );

/**
* Post Authors Post Link Shortcode
*
* @author David Gray, adapted from Bill Erickson
* @link http://iamdavidgray.com/how-to-display-multiple-authors-in-a-post, adapted from http://www.billerickson.net/wordpress-post-multiple-authors/
*
* @param array $atts
* @return string $authors
*/
function be_post_the_authors_link_shortcode( $atts ) {

$atts = shortcode_atts( array(
'between' => null,
'between_last' => null,
'before' => null,
'after' => null
), $atts );

$authors = function_exists( 'coauthors_links' ) ? coauthors_links( $atts['between'], $atts['between_last'], $atts['before'], $atts['after'], false ) : $atts['before'] . get_the_author_link() . $atts['after'];
return $authors;
}
add_shortcode( 'post_the_authors_link', 'be_post_the_authors_link_shortcode' );

